```ts
const userStore = new Map<string, { roles: Role[] }>([
  ['adminUser', { roles: ['admin'] }],
  ['scraperUser', { roles: ['scraper'] }],
  ['viewerUser', { roles: ['viewer'] }],
]);

export function getUserRoles(userId: string): Role[] {
  return userStore.get(userId)?.roles || [];
}

export function userHasPermission(userId: string, agentName: string): boolean {
  const roles = getUserRoles(userId);
  return roles.some(role => rolePermissions[role]?.includes(agentName) || rolePermissions[role]?.includes('*'));
