// agentic-system/index.ts
// Godmode Agentic Orchestrator Core (No-Install, Self-Contained)

import fs from 'fs';
import http from 'http';
import url from 'url';

// ================================
// TYPE DEFINITIONS
// ================================
export interface Agent {
  name: string;
  run: (action: string, userId: string) => Promise<any>;
  onFail?: (action: string, userId: string, error: Error) => Promise<any>;
}

export interface AgentAction {
  agent: string;      // Key in agents registry
  action: string;     // The operation/task/intent
  userId: string;     // The caller/user ID
}

// ================================
// GLOBAL STATE
// ================================
let panicLock = false;
let panicExpiresAt = 0;
const chaosLimiter: Record<string, number> = {};
const requestCounts: Map<string, number[]> = new Map();

const PANIC_THRESHOLD = 5;
const PANIC_DURATION_MS = 5 * 60 * 1000; // 5 minutes

// ================================
// STATE FILE PERSISTENCE
// ================================
const stateFile = 'panicState.json';
function loadState() {
  if (fs.existsSync(stateFile)) {
    const state = JSON.parse(fs.readFileSync(stateFile, 'utf-8'));
    panicLock = state.panicLock;
    panicExpiresAt = state.panicExpiresAt;
    Object.assign(chaosLimiter, state.chaosLimiter);
  }
}

function saveState() {
  const state = {
    panicLock,
    panicExpiresAt,
    chaosLimiter
  };
  fs.writeFileSync(stateFile, JSON.stringify(state, null, 2));
}

loadState();

// ================================
// SAFE EXECUTOR
// ================================
export async function safeExecuteAgent(agent: Agent, action: string, userId: string) {
  const now = Date.now();

  if (panicLock && now < panicExpiresAt) {
    return { error: 'System in panic mode. Try again later.' };
  } else if (panicLock) {
    panicLock = false;
    chaosLimiter[agent.name] = 0;
  }

  try {
    const result = await agent.run(action, userId);
    chaosLimiter[agent.name] = 0;
    saveState();
    return result;
  } catch (err) {
    logEvent('agent.fail', { agent: agent.name, action, error: err.message });

    chaosLimiter[agent.name] = (chaosLimiter[agent.name] || 0) + 1;
    if (chaosLimiter[agent.name] >= PANIC_THRESHOLD) {
      panicLock = true;
      panicExpiresAt = Date.now() + PANIC_DURATION_MS;
      logEvent('panic.locked', { agent: agent.name });
    }
    saveState();

    if (agent.onFail) {
      try {
        return await agent.onFail(action, userId, err);
      } catch (fallbackErr) {
        return { error: 'Fallback failed too.' };
      }
    }
    return { error: 'Unhandled agent failure' };
  }
}

// ================================
// RATE LIMITER
// ================================
export function rateLimit(key: string, limit = 10, windowMs = 60000): boolean {
  const now = Date.now();
  const times = requestCounts.get(key) || [];
  const recent = times.filter(t => now - t < windowMs);

  if (recent.length >= limit) return false;

  requestCounts.set(key, [...recent, now]);
  return true;
}

// ================================
// AUTO-EXEC LOOP (TaskGPT)
// ================================
export function taskGPTLoop(runCulturalScan: () => void) {
  const loop = () => {
    if (panicLock) return;

    try {
      if (process.env.CHAOS_TEST === 'true' && Math.random() < 0.2) {
        throw new Error('Chaos monkey injected error');
      }
      runCulturalScan();
    } catch (err) {
      logEvent('task.scan.error', { error: err.message });
    } finally {
      setTimeout(loop, 10 * 60 * 1000);
    }
  };
  setTimeout(loop, 1000);
}

// ================================
// LOCAL LOGGING
// ================================
export function logEvent(event: string, payload = {}) {
  const entry = {
    timestamp: new Date().toISOString(),
    event,
    ...payload
  };
  fs.appendFileSync('eventlog.json', JSON.stringify(entry) + '\n');
}

// ================================
// ADMIN RESET TOOL
// ================================
export function resetSystem() {
  panicLock = false;
  panicExpiresAt = 0;
  for (const key in chaosLimiter) chaosLimiter[key] = 0;
  requestCounts.clear();
  saveState();
  logEvent('system.reset', {});
}

// ================================
// AGENT REGISTRY
// ================================
export const agents: Record<string, Agent> = {
  echoAgent: {
    name: 'echoAgent',
    run: async (action, userId) => ({ echo: `Agent ran: ${action} by ${userId}` }),
    onFail: async (action, userId, err) => ({ fallback: `Recovery for ${action}` })
  }
};

// ================================
// ADMIN SERVER (Minimal API)
// ================================
const server = http.createServer(async (req, res) => {
  const parsedUrl = url.parse(req.url || '', true);
  if (req.method === 'POST' && parsedUrl.pathname === '/admin/reset') {
    resetSystem();
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(JSON.stringify({ status: 'reset successful' }));
  } else if (req.method === 'GET' && parsedUrl.pathname === '/admin/state') {
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(fs.readFileSync(stateFile, 'utf-8'));
  } else {
    res.writeHead(404);
    res.end('Not Found');
  }
});

server.listen(8787, () => {
  console.log('Admin server running at http://localhost:8787');
});

