## üß† Step 4.1 ‚Äî **Mini Execution Loop (Function Resolver)**

```ts
// assistantRunner.ts
import { openai } from "./openaiClient"

export async function runAssistantWithFunction(
  assistant_id: string,
  user_input: string,
  functions: Record<string, (args: any) => Promise<any>>
) {
  const thread = await openai.beta.threads.create()

  await openai.beta.threads.messages.create(thread.id, {
    role: "user",
    content: user_input
  })

  const run = await openai.beta.threads.runs.create(thread.id, {
    assistant_id
  })

  let runStatus = await openai.beta.threads.runs.retrieve(thread.id, run.id)

  while (runStatus.status === "in_progress" || runStatus.status === "queued") {
    await new Promise(res => setTimeout(res, 1000))
    runStatus = await openai.beta.threads.runs.retrieve(thread.id, run.id)
  }

  if (runStatus.status === "requires_action") {
    const funcCall = runStatus.required_action?.submit_tool_outputs.tool_calls?.[0]
    const { name, arguments: args } = funcCall?.function || {}

    const parsedArgs = JSON.parse(args || "{}")
    const result = await functions[name](parsedArgs)

    await openai.beta.threads.runs.submitToolOutputs(thread.id, run.id, {
      tool_outputs: [
        {
          tool_call_id: funcCall.id,
          output: JSON.stringify(result)
        }
      ]
    })

    return await waitForFinalResponse(thread.id, run.id)
  }

  return runStatus
}

async function waitForFinalResponse(thread_id: string, run_id: string) {
  let finalRun = await openai.beta.threads.runs.retrieve(thread_id, run_id)

  while (finalRun.status === "in_progress") {
    await new Promise(res => setTimeout(res, 1000))
    finalRun = await openai.beta.threads.runs.retrieve(thread_id, run_id)
  }

  const messages = await openai.beta.threads.messages.list(thread_id)
  return messages.data?.[0]?.content?.[0]?.text?.value || "[No output]"
}
```

