// extract-rituals.mjs
import fs from 'fs'
import path from 'path'
import readline from 'readline'

// === üõ†Ô∏è CONFIG ===
const notebookPath = './notebook-dump.txt'
const outputPath = './triggerMap.json'

// Your local LLM endpoint (adjust for LM Studio, Ollama, etc.)
const LLM_API_URL = 'http://localhost:11434/api/generate'

// === üì¶ RITUAL EXTRACTION PROMPT ===
const basePrompt = `
You are an extraction module. Given a notebook dump with embedded rituals and archetypes, extract a flat triggerMap in JSON format.

Format:
{
  "summon-lore": {
    "function": "generateLoreDrop",
    "parameters": {
      "archetype": "Whoracism"
    }
  }
}

Only extract entries that contain clear trigger names or emoji markers.

Input:
`;

// === üöÄ EXECUTION ===
async function extractTriggerMap() {
  const notebookText = fs.readFileSync(notebookPath, 'utf-8')
  const fullPrompt = basePrompt + notebookText

  const response = await fetch(LLM_API_URL, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      prompt: fullPrompt,
      model: 'gpt4', // or whatever you‚Äôve loaded locally
      stream: false
    })
  })

  const result = await response.json()
  const jsonText = result.response.trim()

  // Optional: try parsing to validate JSON
  try {
    const parsed = JSON.parse(jsonText)
    fs.writeFileSync(outputPath, JSON.stringify(parsed, null, 2))
    console.log(`‚úÖ Rituals extracted and saved to ${outputPath}`)
  } catch (err) {
    console.error('‚ùå Failed to parse response as JSON:')
    console.error(jsonText)
  }
}

extractTriggerMap()
```

