export default async function handler(req, res) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  const { title, type, payload } = req.body
  if (!title || !type) {
    return res.status(400).json({ error: 'Missing fields' })
  }

  // Example: write to database or fire drop engine
  console.log('Triggering drop:', { title, type, payload })

  return res.status(200).json({ status: 'Drop triggered', title })
}

// /api/rituals.js
export default async function handler(req, res) {
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  const rituals = [
    { id: 1, title: 'ok so i cried', type: 'zine' },
    { id: 2, title: 'satirical grief loop', type: 'live event' }
  ]

  return res.status(200).json(rituals)
}

// /api/run-inference.js
export default async function handler(req, res) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  const { prompt } = req.body
  if (!prompt) {
    return res.status(400).json({ error: 'Missing prompt' })
  }

  const response = `Generated ritual for: ${prompt}`
  return res.status(200).json({ output: response })
}

// /api/zines.js
export default async function handler(req, res) {
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' })
  }

  const zines = [
    { id: 101, title: 'ok so i cried', format: 'pdf', link: '/zines/ok-so-i-cried.pdf' }
  ]

  return res.status(200).json(zines)
}"
}

