export function RoleBadge({ role }) {
  return (
    <span style={{ background: role.themeColor }}>
      {role.icon} {role.name}
    </span>
  )
}

// UI: ZineCard.js
export function ZineCard({ zine }) {
  if (!zine.public) return null
  return (
    <div className="zine-card">
      <h3>{zine.tags.join(', ')}</h3>
      <a href={zine.fileUrl} target="_blank">Download</a>
    </div>
  )
}

// UI: RitualFeedItem.js
