import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"

export default function App() {
  const [rituals, setRituals] = useState([
    { title: "ok so i cried", mood: "grief-clarity", roles: ["mysconduct"] },
    { title: "launch fragment", mood: "aligned-fury", roles: ["satirium"] }
  ])
  const [zines, setZines] = useState([
    { title: "ok so i cried", link: "/zines/ok-so-i-cried.pdf" }
  ])
  const [reactions, setReactions] = useState({})

  const handleReact = (index, emoji) => {
    const updated = { ...reactions }
    updated[index] = emoji
    setReactions(updated)
  }

  return (
    <main className="p-6 grid gap-4">
      <h1 className="text-3xl font-bold">HelenKella Ritual Mockup</h1>

      <section>
        <h2 className="text-xl font-semibold mb-2">🌀 Ritual Queue</h2>
        <div className="grid gap-3">
          {rituals.map((ritual, i) => (
            <Card key={i}>
              <CardContent className="p-4">
                <h3 className="text-lg font-bold">{ritual.title}</h3>
                <p className="text-sm italic text-muted">{ritual.mood}</p>
                <div className="mt-2 text-sm">Roles: {ritual.roles.join(", ")}</div>
                <div className="mt-2 space-x-2">
                  <Button onClick={() => handleReact(i, "🕯️")}>🕯️</Button>
                  <Button onClick={() => handleReact(i, "💀")}>💀</Button>
                  <Button onClick={() => handleReact(i, "🔥")}>🔥</Button>
                  <span className="ml-2">{reactions[i]}</span>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-xl font-semibold mt-6 mb-2">📜 Zine Gallery</h2>
        <div className="grid gap-3">
          {zines.map((z, i) => (
            <Card key={i}>
              <CardContent className="p-4">
                <h3 className="text-md font-semibold">{z.title}</h3>
                <a className="text-blue-600 underline" href={z.link} target="_blank">Open PDF</a>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>
    </main>
  )
}"
}

