// Run this with Node.js (e.g. node server.js)

import express from 'express'
const app = express()
app.use(express.json())

// 1. Fake key DB
const VALID_KEYS = {
  'sk-helen-secret': {
    user: 'helen',
    quota: 100,
    usage: 0,
    plan: 'creator+',
  }
}

// 2. Middleware to check API key
function checkAPIKey(req, res, next) {
  const key = req.headers['authorization']?.replace('Bearer ', '')
  if (!key || !VALID_KEYS[key]) {
    return res.status(401).json({ error: 'Invalid or missing API key' })
  }

  req.apiUser = VALID_KEYS[key] // attach to request
  next()
}

// 3. Route that simulates Gemini function
