### **1. `DiscordOpsDashboard.jsx`**

```jsx
import React, { useState, useEffect } from "react";

export default function DiscordOpsDashboard() {
  const [command, setCommand] = useState("");
  const [param, setParam] = useState("");
  const [logs, setLogs] = useState([]);
  const [status, setStatus] = useState("");

  // Fetch logs on mount
  useEffect(() => {
    fetch("/api/logs")
      .then((res) => res.json())
      .then((data) => setLogs(data))
      .catch(() => setLogs([]));
  }, []);

  const handleSend = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    try {
      const res = await fetch("/api/command", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ command, param }),
      });
      const data = await res.json();
      setStatus(data.message || "Sent!");
    } catch {
      setStatus("Failed to send command.");
    }
  };

  return (
    <main className="min-h-screen bg-neutral-950 text-neutral-100 p-8 flex flex-col items-center">
      <section className="max-w-lg w-full">
        <h1 className="text-3xl font-bold mb-6">BLACKFORGE DISCORD OPS</h1>
        <form className="flex flex-col gap-4 mb-8" onSubmit={handleSend}>
          <label className="flex flex-col gap-1">
            <span className="font-medium">Command</span>
            <select
              value={command}
              onChange={(e) => setCommand(e.target.value)}
              className="p-2 rounded bg-neutral-900 border border-neutral-800"
              required
            >
              <option value="">Select command</option>
              <option value="ship">/ship</option>
              <option value="vent">/vent</option>
              <option value="meme">/meme</option>
              <option value="graveyard">/graveyard</option>
              <option value="nobricked">/nobricked</option>
              <option value="opslog">/opslog</option>
              <option value="ask">/ask (GPT)</option>
            </select>
          </label>
          {(command === "ship" || command === "ask") && (
            <label className="flex flex-col gap-1">
              <span className="font-medium">
                {command === "ship" ? "Project Name" : "Prompt"}
              </span>
              <input
                type="text"
                value={param}
                onChange={(e) => setParam(e.target.value)}
                className="p-2 rounded bg-neutral-900 border border-neutral-800"
                required
              />
            </label>
          )}
          <button
            type="submit"
            className="bg-pink-600 hover:bg-pink-500 rounded-xl p-3 font-bold text-lg shadow"
          >
            Send Command
          </button>
          <div className="text-sm mt-2 h-6">{status}</div>
        </form>

        <section>
          <h2 className="text-xl font-semibold mb-2">Recent Logs</h2>
          <div className="bg-neutral-900 rounded-xl p-4 max-h-64 overflow-y-auto space-y-2">
            {logs.length === 0 ? (
              <p className="text-neutral-500">No logs yet.</p>
            ) : (
              logs.map((log, i) => (
                <div key={i} className="border-b border-neutral-800 pb-2 mb-2">
