```ts
import express from 'express';
import cors from 'cors';
import bodyParser from 'body-parser';
import { agents } from './agents/registry';
import { safeExecuteAgent } from './agents/safeExecuteAgent';

const app = express();
app.use(cors());
app.use(bodyParser.json());

app.post('/execute', async (req, res) => {
  const { agentName, action, userId, context } = req.body;
  const agent = agents[agentName];
  if (!agent) return res.status(404).json({ error: 'Unknown agent' });

  const result = await safeExecuteAgent(agent, action, userId, context);
  res.json(result);
});

app.listen(4000, () => console.log(`Agent server running on http://localhost:4000`));
```

