import { useState } from 'react';
import axios from 'axios';

const roles = ['admin', 'scraper', 'viewer'];
const agents = ['notebookScraperAgent', 'urlScraperAgent'];

export default function App() {
  const [userId, setUserId] = useState('scraperUser');
  const [agent, setAgent] = useState(agents[0]);
  const [context, setContext] = useState('{"pageUrl": "https://your.notebook.lm/test"}');
  const [result, setResult] = useState<any>(null);

  const runAgent = async () => {
    const res = await axios.post('http://localhost:4000/execute', {
      agentName: agent,
      action: 'run',
      userId,
      context: JSON.parse(context),
    });
    setResult(res.data);
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-xl font-bold">🧪 Godmode Agent Dev GUI</h1>

      <div className="my-4">
        <label>User Role:</label>
        <select value={userId} onChange={e => setUserId(e.target.value)} className="ml-2 border p-1">
          {roles.map(r => <option key={r} value={`${r}User`}>{r}</option>)}
        </select>
      </div>

      <div className="my-2">
        <label>Agent:</label>
        <select value={agent} onChange={e => setAgent(e.target.value)} className="ml-2 border p-1">
          {agents.map(a => <option key={a} value={a}>{a}</option>)}
        </select>
      </div>

      <textarea
        value={context}
        onChange={e => setContext(e.target.value)}
        className="w-full border p-2 my-2"
        rows={5}
      />

      <button onClick={runAgent} className="bg-blue-500 text-white px-4 py-2 rounded">Run Agent</button>

      <pre className="bg-black text-green-400 p-4 mt-4 rounded text-sm overflow-auto">
        {result ? JSON.stringify(result, null, 2) : '// result will appear here'}
      </pre>
    </div>
  );
}
```

> Use Tailwind CSS for styling or swap for Chakra if you prefer.

