## 🧪 DEMO GUI: Vite + Tailwind + Agent Form Renderer

### File: `frontend/src/App.tsx`

```tsx
import { useState } from 'react';
import axios from 'axios';

const AGENTS = [
  {
    name: "notebookScraperAgent",
    fields: [{ key: "pageUrl", type: "url" }]
  }
];

export default function App() {
  const [userId, setUserId] = useState("discordUser123");
  const [selectedAgent, setSelectedAgent] = useState(AGENTS[0]);
  const [context, setContext] = useState({ pageUrl: "" });
  const [result, setResult] = useState<any>(null);

  const handleRun = async () => {
    const res = await axios.post("http://localhost:4000/execute", {
      agentName: selectedAgent.name,
      action: "fetch",
      userId,
      context
    });
    setResult(res.data);
  };

  return (
    <div className="max-w-2xl p-6 mx-auto">
      <h1 className="text-xl font-bold mb-4">🧪 Agentic Dev GUI</h1>

      <select onChange={e => setSelectedAgent(AGENTS.find(a => a.name === e.target.value)!)}>
        {AGENTS.map(agent => (
          <option key={agent.name}>{agent.name}</option>
        ))}
      </select>

      {selectedAgent.fields.map(field => (
        <input
          key={field.key}
          type={field.type}
          placeholder={field.key}
          value={context[field.key] || ""}
          onChange={e => setContext(prev => ({ ...prev, [field.key]: e.target.value }))}
          className="block border p-2 my-2 w-full"
        />
      ))}

      <button className="bg-blue-500 text-white px-4 py-2" onClick={handleRun}>Run</button>

      <pre className="bg-black text-green-300 p-4 mt-4 rounded text-sm">{JSON.stringify(result, null, 2)}</pre>
    </div>
  );
}
```

