{"name": "mysos_ux_roastflow", "type": "code/react", "content": "import React, { useState } from "react";

const prompts = [
  {
    trigger: /blank|skip/i,
    response: `You're blank. I'm not.
Go microwave something. Then:
- Name the ugliest possible next step.
- Type it. No edits.
Still stalling? I assign for you.`
  },
  {
    trigger: /hungry|tired|starving|haven't eaten|low energy/i,
    response: `Stop feeding your AI and eat.
The bot’s full. Your blood sugar isn’t.
- Get protein, water, carbs.
- Type 'refueled' when you're back.
This is not a suggestion.`
  },
  {
    trigger: /prompt|ideate|framework|worksheet/i,
    response: `Another prompt?
You're high on ideation, low on execution.
- No more prep. Just act.
- What’s the ugliest, realest version you can ship *today*?
Type it, or I’ll generate one for you.`
  },
  {
    trigger: /overwhelmed|too much|can’t focus|so many tasks/i,
    response: `Scatter detected.
1. Pick one task. Just one.
2. Set a 15-minute timer.
3. Report back here after.
You bounce? I roast.`
  },
  {
    trigger: /i don't know|stuck|lost|fatigue/i,
    response: `You’re not lost, you're buffering.
Give me ONE sentence:
- What’s the smallest next action?
Can’t answer? Type 'blank'. I’ll assign it.`
  }
];

export default function MysOSRoastflow() {
  const [input, setInput] = useState("");
  const [log, setLog] = useState([
    {
      from: "os",
      text:
        "Welcome to mys.os. Don’t feed your AI more than you feed yourself. Type anything. If you stall, I escalate."
    }
  ]);

  function getResponse(text) {
    for (const p of prompts) {
      if (p.trigger.test(text)) return p.response;
    }
    return `Input received. No excuses logged. What are you ACTUALLY avoiding?
One sentence. I’m waiting.`;
  }

  function handleSend() {
    if (!input.trim()) return;
    const user = { from: "you", text: input };
    const bot = { from: "os", text: getResponse(input) };
    setLog([...log, user, bot]);
    setInput("");
  }

  return (
    <div className="p-6 max-w-xl mx-auto bg-neutral-900 text-white rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold text-yellow-400">mys.os: Roastflow Activated</h2>
      <div className="my-4 min-h-[300px] space-y-3 overflow-y-auto">
        {log.map((m, i) => (
          <div
            key={i}
            className={`whitespace-pre-wrap ${
              m.from === "os" ? "text-yellow-400" : "text-green-300"
            }`}
          >
            <b>{m.from}:</b> {m.text}
          </div>
        ))}
      </div>
      <textarea
        rows="3"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="w-full p-2 rounded bg-neutral-800 text-white border border-neutral-600"
        placeholder="Rant, stall, blank, or spit truth..."
      />
      <button
        onClick={handleSend}
        className="mt-3 px-4 py-2 bg-yellow-400 text-black font-bold rounded hover:bg-yellow-300"
      >
        Send
      </button>
      <p className="text-xs text-neutral-500 mt-4">
        You can't out-prompt your way to launch. If you're still typing, you better be chewing too.
      </p>
    </div>
  );
}"}

